INSERT INTO guides (id, created_at, updated_at, is_active, uuid, record_number, date_of_issue, phone_number, email, bio, user_id, region_id, language_id) VALUES
    (1, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb26', '10ΣΞ00084Ε0', '2018-07-30', '6930101001', 'alexpap@gmail.com', 'Είμαι διπλωματούχος ξεναγός απόφοιτος της σχολής ξεναγών Αθηνών και καθηγητής τουρκικής γλώσσας. Παρέχω υπηρεσίες ξεναγού σε όλη την Ελλάδα με επίκεντρο τον άνθρωπο και την διάδραση του στον χωροχρόνο,εμβαθύνοντας στους τομείς της ιστορίας, της τέχνης, της μυθολογίας, της εθνολογίας και της λαογραφίας. Απευθύνομαι σε ιδιώτες, εταιρείες, οργανισμούς  οικογένειες ή σχολεία. Ελάτε να ανακαλύψουμε την ομορφιά και τις ιδιαιτερότητες της χώρας μας μαζί!', 2, 2, 1),
    (2, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb27', '33ΤΑ02384Ε1', '2018-07-30', '6930101002', 'guide2@guides.gr', 'Είμαι απόφοιτη του τμήματος Ιστορίας και Αρχαιολογίας του ΕΚΠΑ και ακολούθησα μετεκπαίδευση στη σχολή Ξεναγών. Είμαι ενεργή τα τελευταία 12 χρόνια και έχω αναλάβει πολυάριθμες ξεναγήσεις σε αρχαιολογικούς χώρους και μουσεία.', 3, 5, 6),
    (3, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb28', '07ΣΞ00162Ε0', '2018-07-30', '6930101003', 'guide3@guides.gr', 'Είμαι πιστοποιημένη ξεναγός τα τελευταία 15 χρόνια και διαθέτω μεγάλη εμπειρία τόσο σε ατομικές ξεναγήσεις όσο και στην ξενάγηση γκρουπ.', 4, 3, 1),
    (4, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb29', '08ΣΞ01253Ε0', '2018-07-30', '6930101004', 's.papadopoulos@yahoo.com', '', 5, 6, 10),
    (5, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb30', '10ΣΞ00120Ε1', '2018-07-30', '6930101005', 'guide5@guides.gr', '', 6, 12, 6),
    (6, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb31', '25ΤΑ01525Ε0', '2018-07-30', '6930101006', 'jmourvati@gmail.com', 'Αποφοίτησα από την σχολή ξεναγών το 1987. Αγάπησα το επάγγελμα αυτό από την πρώτη στιγμή. Έχω παρακολουθήσει πολλά σεμινάρια με θέμα τον Ελληνικό πολιτισμό στην Ελλάδα και στο εξωτερικό. Παράλληλα, έχω δώσει διαλέξεις σε φοιτητές του Πολυτεχνείου, σε αποφοίτους γνωστών Ivy League πανεπιστημίων (Harvard, Yale, Brown) και σε πολιτιστικούς συλλόγους.', 7, 7, 14),
    (7, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb32', '11ΣΞ12584Ε0', '2018-07-30', '6930101007', 'guide7@guides.gr', '', 8, 1, 1),
    (8, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb33', '04ΤΑ00090Ε1', '2018-07-30', '6930101008', 'guide8@guides.gr', 'Αποφοίτησα από τη σχολή Ξεναγών της Αθήνας το 2006, αφότου ολοκλήρωσα τις σπουδές μου σε προπτυχιακό και μεταπτυχιακό επίπεδο στην Ελλάδα και την Ισπανία (Ευρωπαϊκή Ιστορία και Μετάφραση). Κατά τη διάρκεια της επαγγελματικής μου σταδιοδρομίας, είχα την τύχη να ξεναγήσω επανειλλημένως διεθνείς προσωπικότητες από τον καλλιτεχνικό, πολιτικό και αθλητικό χώρο.', 9, 2, 10),
    (9, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb34', '10ΣΞ00170Ε0', '2018-07-30', '6930101009', 'dionisis.efthimiadis@gmail.com', 'Είμαι απόφοιτος του τμήματος Γαλλικής Φιλολογίας και ακολούθησα μετεκπαίδευση στη σχολή Ξεναγών. Αναλαμβάνω ξεναγήσεις γκρουπ στην Γαλλική και την Ελληνική γλώσσα', 10, 2, 6),
    (10, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb35', '10ΣΞ02547Ε0', '2018-07-30', '6930101010', 'guide10@guides.gr', '', 11, 1, 3),
    (11, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb36', '03ΤΑ00284Ε1', '2018-07-30', '6930101011', 'guide11@guides.gr', 'Σπούδασα Ελληνική Φιλολογία με ειδίκευση στην Γλωσσολογία στη Φιλοσοφική Σχολή του Πανεπιστημίου Αθηνών (1995-1999), και στη συνέχεια φοίτησα στη Σχολή Ξεναγών της Αθήνας (2000-2002), ενώ στις μεταπτυχιακές σπουδές μου στο Πανεπιστήμιο Paris VIII του Παρισιού ασχολήθηκα με τη Γλωσσολογία και την κατάκτηση της μητρικής γλώσσας από τα παιδιά. Από το 2003 εξασκώ το επάγγελμα του ξεναγού με συνέπεια και αγάπη, ξεναγώντας οργανωμένα γκρουπ και μεμονωμένους επισκέπτες στην ηπειρωτική Ελλάδα και τα νησιά. Οι Μυκήνες, η Επίδαυρος, οι Δελφοί, ο Μυστράς και η Ολυμπία, αλλά και το Ακρωτήρι στην Σαντορίνη, η Δήλος, η Βεργίνα και η Καστοριά είναι κάποιοι από τους χώρους όπου συχνά ξεναγώ, συνοδεύοντας γκρουπ σε προγράμματα περιηγητικού τουρισμού.', 12, 2, 6),
    (12, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 0, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb37', '09ΤΑΞ00784Ε1', '2018-07-30', '6930101012', 'guide12@guides.gr', '', 13, 2, 1),
    (13, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb38', '10ΣΞ00784Ε0', '2018-07-30', '6930101013', 'guide13@guides.gr', 'Εργάζομαι ως διπλωματούχος ξεναγός από το 1999. Με την πολυετή εμπειρία μου, ξεναγώντας σε όλη την Ελληνική επικράτεια, είμαι σε θέση να σας προσφέρω μια επαγγελματική καθοδήγηση στον χώρο-μνημείο που επιθυμείτε, παρέχοντας πληροφορίες αρχαιολογικού, ιστορικού, λαογραφικού και θρησκευτικού περιεχομένου σχετικά με αυτόν. Αναλαμβάνω υπεύθυνα τις περιηγήσεις  σχολείων, συλλόγων, ΚΑΠΗ, σωματείων, γκρούπ θρησκευτικού τουρισμού καθώς και όλους τους λάτρεις του αρχαιού Ελληνικού πολιτισμού. Το πρόγραμμα και το περιεχόμενο της ξενάγησης προσαρμόζεται στις επιλογές και τα ενδιαφέροντά σας.', 14, 2, 3),
    (14, '2025-07-30 10:00:00', '2025-07-30 10:00:00', 1, 'ef668c53-e8ee-48c1-bcf5-47e87bafeb39', '10ΣΞ02084Ε0', '2018-07-30', '6930101014', 'dapostolidis@yahoo.com', '', 15, 10, 20);
